InstallScript :=		func(partFrame)							begin								RegUserConfigChange(kAppSymbol, partFrame.theForm.MAutoRegister);								AddDeferredCall(partFrame.theForm.MAutoRegister, [kInetServicesSymbol]);							end;RemoveScript :=	func(partFrame)							begin								UnRegUserConfigChange(kAppSymbol);								if GlobalFnExists('UnRegInetLinkStatusChange) then									UnRegInetLinkStatusChange(kAppSymbol);							end;